import React,{useState} from 'react'
import PropTypes from 'prop-types'

export default function TextArea(props) {
    const [text,setText]=useState('Enter Your Text')

let convert=()=>{
    setText(text.toUpperCase());
}
let change=(event)=>{
    setText(event.target.value);
}
  return (
    <div className='container'>
      <div class="mb-3">
  <label for="exampleFormControlTextarea1" className="form-label">{props.title}</label>
  <textarea className="form-control" id="Body" rows="3" onChange={change}></textarea>
</div>
<button className='ConverterToUpperCase' onClick={convert}>Convert To Upper Case</button>
    </div>
  )
}
TextArea.propTypes={
    title: PropTypes.string
}
TextArea.DefaulProps={
    title: 'Enter Text'
}
